generated.css:
*,
*::before,
*::after {
  color: #333;
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

a:hover {
  text-decoration: none;
}

p {
  margin: 0;
}

li {
  list-style: none;
}

h1, h2, h3, h4, h5, h6 {
  margin: 0;
  font-weight: normal;
  font-weight: 1rem;
}

body {
  background-color: #f0f4f5;
  color: #333;
  margin: 0;
}

img {
  max-width: 100%;
}

.c--section {
  padding: 1rem;
  width: 100%;
  max-width: 900px;
  margin: 0 auto;
}

.c--section-title {
  font-size: 2rem;
  max-width: 900px;
  margin: 0;
  width: 100%;
}

.c--section .skill-content {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}
.c--section .skill-content dl {
  background-color: #FFF4E1;
  border: 3px solid #E67A7A;
  border-radius: 3px;
  width: 33%;
  margin: 1rem;
  padding: 1rem;
}

.c--section.product {
  /*background-color: #FFF4E1;*/
  width: 100%;
}

.c--experiment-list {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  width: 100%;
}
.c--experiment-list li {
  width: 100%;
}
@media screen and (min-width: 544px) {
  .c--experiment-list li {
    width: 47%;
  }
}
.c--experiment-list li .experiment-container {
  /*background-color: #fff;*/
  background-color: #21242d;
  border-radius: 5px;
  box-shadow: 0 20px 30px -15px rgba(0, 0, 0, 0.08);
  margin: 0 0 40px;
  padding: 1rem;
  width: 100%;
}
.c--experiment-list li .experiment-container div {
  border-bottom: 1px solid #313746;
  color: #fff;
}
.c--experiment-list li .experiment-container div:last-of-type {
  border: none;
}
.c--experiment-list li .experiment-container .year {
  font-size: 0.8rem;
  opacity: 0.7;
}
.c--experiment-list li .experiment-container .position {
  font-size: 0.6rem;
}
.c--experiment-list li .experiment-container .tech {
  font-size: 0.6rem;
}
@media screen and (min-width: 544px) {
  .c--experiment-list li:nth-child(1) .experiment-container {
    margin: 0;
  }
  .c--experiment-list li:nth-child(2) .experiment-container {
    margin: 60px 0 0;
  }
  .c--experiment-list li:nth-child(3) .experiment-container {
    margin: -30px 0 0;
  }
}

.c--works-list {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  width: 100%;
}
.c--works-list li {
  box-shadow: 0 20px 30px -15px rgba(0, 0, 0, 0.08);
  margin-bottom: 50px;
  width: 100%;
}
@media screen and (min-width: 544px) {
  .c--works-list li {
    margin-bottom: 0;
    width: 47%;
  }
}
.c--works-list li:last-of-type {
  margin-bottom: 0;
}
.c--works-list li a {
  display: block;
}
.c--works-list li .work-image {
  border-radius: 5px 5px 0 0;
  object-position: top;
  object-fit: cover;
  height: 300px;
  width: 100%;
}
.c--works-list li .work-text-container {
  border-radius: 0 0 5px 5px;
  background-color: #FFF;
  padding: 1rem;
  width: 100%;
}
.c--works-list li .work-title {
  font-size: 1.4rem;
  width: 100%;
}
.c--works-list li .work-description {
  border-radius: 0 0 5px 5px;
  background-color: #FFF;
  height: 3em;
  padding: 0 0.5rem;
  width: 100%;
}
